import React from "react";

function Food(probs){
  return <h2>{probs.id}. I like {probs.name}</h2>;
}

const foodLike = [
  {
    name : "kimchi",
    id : 1
  },
  {
    name : "banana",
    id : 2
  }
];

function renderFood(dish){
  return <Food name={dish.name} id={dish.id}/>;
}

function App() {
  return (
  <div>
    <h1>Hi!</h1>
    {foodLike.map(renderFood)}
  </div>
  );
}

export default App;
